mapper.delete:
DELETE FROM ABSENCE
WHERE ABSENCE_NO IN

    #{ABSENCE_NO}

mapper.update:
UPDATE ABSENCE SET EMP_ID = #{EMP_ID}, ABSENCE_TYPE = #{ABSENCE_TYPE}, ABSENCE_START = #{ABSENCE_START}, ABSENCE_END = #{ABSENCE_END},
ABSENCE_REMARK = #{ABSENCE_REMARK},
HIGH_APPROVAL = #{HIGH_APPROVAL}, UPDATE_DATE = CURRENT_TIMESTAMP, UPDATE_EMP_ID = #{UPDATE_EMP_ID} WHERE ABSENCE_NO = #{ABSENCE_NO}
