mapper.select:
SELECT emp_id, emp_role, emp_dept, emp_no
FROM employees
WHERE emp_id = #{value}

SELECT emp.emp_id AS "empId" , emp.emp_name AS "empNm" , dept.common_detail_name AS "deptNm" , TO_CHAR(cmt.cmt_date, 'YYYY-MM-DD') AS "cmtDate" , TO_CHAR(cmt.work_bgn, 'HH24:MI') AS "workBgn" , TO_CHAR(cmt.work_end, 'HH24:MI') AS "workEnd" , stts.common_detail_name AS "sttsNm" , cmt.rmrk AS "rmrk" , cmt.emp_no AS "empNo" FROM cmt_mng cmt LEFT JOIN employees emp ON cmt.emp_no = emp.emp_no LEFT JOIN common_detail dept ON CMT.emp_dept_cd = dept.common_detail_code LEFT JOIN common_detail stts ON cmt.atndn_stts_cd = stts.common_detail_code
AND cmt.emp_dept_cd = #{empDept}
AND cmt.emp_no = #{empNo}
AND cmt.cmt_date BETWEEN #{bgnDate} AND #{endDate}
AND (emp.emp_id LIKE '%' || #{empInfo} || '%' OR emp.emp_name LIKE '%' || #{empInfo} || '%')
ORDER BY cmt.emp_no, cmt.cmt_date DESC
