mapper.insert:
INSERT INTO equipment_info (
	info_no
  , info_sn
  , info_nm
  , info_kind
  , info_mnfct
  , info_buy_date
  , info_set_date
  , info_useyn
  , info_reg_date

) VALUES ( 
	equipment_info_seq.NEXTVAL
  , #{sn}
  , #{name}
  , #{kind}
  , #{mnfct}
  , TO_DATE(#{buy}, 'YYYY-MM-DD')
  , TO_DATE(#{set}, 'YYYY-MM-DD')
  , #{useYn}
  , CURRENT_DATE
)

mapper.update:
UPDATE equipment_info 
SET   info_sn			= #{sn}
	, info_nm			= #{name}
	, info_kind			= #{kind}
	, info_mnfct		= #{mnfct}
	, info_buy_date		= TO_DATE(#{buy}, 'YYYY-MM-DD')
	, info_set_date		= TO_DATE(#{set}, 'YYYY-MM-DD')
	, info_useyn		= #{useYn}
	, info_mdfn_date	= CURRENT_DATE
WHERE info_no 			= #{no}
