mapper.select:
SELECT o.ORDER_ID , ORDER_DATE , ORDER_EMP , e.EMP_NAME , ORDER_TYPE , o.CLIENT_NO , c.CLIENT_NAME , CASE WHEN COUNT(s.ORDER_ID) > 0 THEN COUNT(s.ORDER_ID) -- sale 테이블에서 개수 카운트 WHEN COUNT(b.ORDER_ID) > 0 THEN COUNT(b.ORDER_ID) -- buy 테이블에서 개수 카운트 ELSE 0 END AS count -- 하나의 컬럼으로 개수 출력 , ORDER_UPDATE FROM ORDERS o JOIN EMPLOYEES e ON o.ORDER_EMP = e.EMP_ID JOIN CLIENT c ON o.CLIENT_NO = c.CLIENT_NO LEFT JOIN SALE s ON o.ORDER_ID = s.ORDER_ID LEFT JOIN BUY b ON o.ORDER_ID = b.ORDER_ID
AND TO_CHAR(o.ORDER_DATE, 'YYYY-MM-DD') = #{reg_date}
AND (e.EMP_NAME LIKE '%' || #{search_word} || '%' OR c.CLIENT_NAME LIKE '%' || #{search_word} || '%')
AND o.ORDER_TYPE = #{check_value}
GROUP BY o.ORDER_ID, o.ORDER_DATE, o.ORDER_EMP, e.EMP_NAME, o.ORDER_TYPE, o.CLIENT_NO, c.CLIENT_NAME, o.ORDER_UPDATE

mapper.insert:
INSERT INTO orders (
	   order_id
	 , order_date
	 , order_emp
	 , order_type
	 , client_no
)
VALUES (
	  #{order_id}
	, SYSTIMESTAMP
	, #{order_emp}
	, #{order_type}
	, #{client_no}
)

INSERT INTO sale (
	   order_id
	 , product_no
	 , sale_unit
	 , sale_vol
	 , sale_deadline
	 , required_materials
	 , sale_status
) VALUES (
	  #{order_id}
	, #{product_no}
	, #{sale_unit}
	, #{sale_vol}
	, #{sale_deadline}
	, #{required_materials}
	, #{sale_status}
)

INSERT INTO buy (
	   order_id
	 , mtr_no
	 , buy_unit
	 , buy_vol
	 , buy_status
) VALUES (
	  #{order_id}
	, #{mtr_no}
	, #{buy_unit}
	, #{buy_vol}
	, #{buy_status}
)
