mapper.select:
SELECT e.emp_id, e.emp_name, ddept.common_detail_name AS emp_dept, dpos.common_detail_name AS emp_position, djop.common_detail_name AS emp_job_type FROM employees e LEFT JOIN common_detail ddept ON e.emp_dept = ddept.common_detail_code LEFT JOIN common_detail dpos ON e.emp_position = dpos.common_detail_code LEFT JOIN common_detail djop ON e.emp_job_type = djop.common_detail_code WHERE 1=1
AND e.emp_job_type = #{ocpt}
AND e.emp_dept = #{dept}
AND ( e.emp_name LIKE '%' || #{emp_info} || '%' OR e.emp_id LIKE '%' || #{emp_info} || '%' )

SELECT *
  FROM work_schd
 WHERE emp_id = #{emp_id}
   AND work_date = #{day}
